void UiDraw() // ∏ﬁ¿Œ ui
{
	char str[20];
	sprintf_s(str, "SCORE : %d", score);
	
	DrawStr(2, 2, str, WHITE);

	char str1[20];
	sprintf_s(str1, "Bullets : %d", bulletscount);
	DrawStr(20, 2, str1, WHITE);



	for (int i = 0; i < 30; i++)
	{
		for (int j = 0; j < 120; j++)
		{
			switch (UIBoard[i][j])
			{
			case 1:
				DrawChar(j, i, ' ', WHITE_BOX);
				break;
			default:
				break;
			}
		}
	}
}